'      contains', &
'      subroutine printit()', &
'         ! print i,j,shift and then i,j, and the result as binary values', &
'          write(*,''(*(g0))'')''I='',i,'' J='',j,'' SHIFT='',shift', &
'          write(*,''(b32.32)'') i,j, dshiftl (i, j, shift)', &
