'         write(*,''(a,b16.16,1x,i0)'')''j=     '',j, j', &
'         write(*,''(a,b16.16,1x,i0)'')''result='',ieor(i,j), ieor(i,j)', &
'', &
'        ! elemental', &
'         print *,''arguments may be arrays. If both are arrays they ''', &
'         print *,''must have the same shape.                        ''', &
