'', &
'         call print_matrix_int(''dim=2'',product(box,dim=2))', &
'', &
'         call print_matrix_int(''dim=3'',product(box,dim=3))', &
'', &
'      endblock WITH_DIM', &
'', &
'      contains', &
'', &
