      call journal('*get_args_fixed_length_scalar_c* value too long for', keyword, 'allowed is', ibug,&
      & 'input string [', values(place), '] is', unlen)
      call mystop(19, '*get_args_fixed_length_scalar_c* value too long')
      string = ''
   endif

